import React, { useState } from "react";

function DurationBar(){
    const [focusDuration, setFocusDuration] = useState(1500);
    const [breakDuration, setBreakDuration] = useState(300);

    const initialState = {
        "focus": 1500,
        "break": 300,
    };

    const [timeStates, setTimeStates] = useState(initialState);

    const convertToMin = (seconds) => seconds / 60;

    function addFocusTime(addTime=true){
        addTime === true ? setFocusDuration(currentTime => currentTime + 300) : setFocusDuration(currentTime => currentTime - 300);
    }

    function addBreakTime(addTime=true){
        addTime === true ? setBreakDuration(currentTime => currentTime + 60) : setBreakDuration(currentTime => currentTime - 60);
    }

    function addTime(timerName="focus", addTime=true){
        if (timerName === "focus"){
            addTime === true ?
            setTimeStates({...timeStates, timerName: timeStates[timerName] + 300}) : console.log("not reached");
        }
    }

    return (
        <div className="row">
        <div className="col">
          <div className="input-group input-group-lg mb-2">
            <span className="input-group-text" data-testid="duration-focus">
              {/* TODO: Update this text to display the current focus session duration */}
              Focus Duration: {convertToMin(timeStates.focus)}
            </span>
            <div className="input-group-append">
              {/* TODO: Implement decreasing focus duration and disable during a focus or break session */}
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="decrease-focus"
                onClick={() => addFocusTime(false)}
              >
                <span className="oi oi-minus" />
              </button>
              {/* TODO: Implement increasing focus duration  and disable during a focus or break session */}
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="increase-focus"
                onClick={() => addTime()}
              >
                <span className="oi oi-plus" />
              </button>
            </div>
          </div>
        </div>
        <div className="col">
          <div className="float-right">
            <div className="input-group input-group-lg mb-2">
              <span className="input-group-text" data-testid="duration-break">
                {/* TODO: Update this text to display the current break session duration */}
                Break Duration: {convertToMin(breakDuration)}
              </span>
              <div className="input-group-append">
                {/* TODO: Implement decreasing break duration and disable during a focus or break session*/}
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="decrease-break"
                  onClick={() => addBreakTime(false)}

                >
                  <span className="oi oi-minus" />
                </button>
                {/* TODO: Implement increasing break duration and disable during a focus or break session*/}
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="increase-break"
                  onClick={() => addBreakTime(true)}

                >
                  <span className="oi oi-plus" />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
}

export default DurationBar;
